import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { compose } from "redux";
import mapDispatchToProps from "../../redux/DispatchToProps";
import mapStateToProps from "../../redux/StateToProps";

class EditContent extends Component {
  onSubmit(e) {
    this.props.textInput(document.getElementById("textcontent").value);
    this.props.history.push("/home");
  }

  render() {
    var namelist = ["ALL", "House", "Game", "Pet", "Other"];
    return (
      <div>
        <div>
          <p>List EditContent</p>
          <div className="d-flex justify-content-between">
            {namelist.map((value, index) => {
              return (
                <input
                  className="w-25 m-3 btn btn-light border rounded text-center"
                  type="button"
                  value={value}
                  id={index}
                />
              );
            })}
          </div>
          <div className="container ">
          <textarea id="textcontent" className="w-25 mh-100 position-absolute top-50 start-50 translate-middle"></textarea>
          <input
            type="button"
            value="submit"
            onClick={this.onSubmit.bind(this)}
            className="position-absolute top-50 start-70"
          />
          </div>
        </div>
      </div>
    );
  }
}

export default compose(
  withRouter,
  connect(mapStateToProps, mapDispatchToProps)
)(EditContent);
