import React, { Component } from 'react'

export default class Login extends Component {

    onSubmit(event){
        if(event.target.value==='Register'){
            var username=document.getElementById('username').value.trim()
            var password=document.getElementById('password').value.trim()
            if(username.length()>1 && password.length()>=8){

            }
         document.getElementById('formRegister').action='/login'
            document.getElementById('formRegister').submit();
        }
    }

    render() {
        return (
            <div>
                <div>Register</div>
                <form  method="POST" id="formRegister">
                    <label for="username">username</label>                    
                    <input type="email" placeholder='example@hotmail.com' id="username" name="username"></input><br/>
                    <label for="password">password</label>                    
                    <input type="password" placeholder="least 8 word" id="password" name="password"></input><br/>
                    <input type="button" value="Register" onClick={this.onSubmit.bind(this)}></input>
                </form>
                <div>Login</div>
                <form method="POST" id="formLogin">
                    <label for="username">username</label>                    
                    <input type="email" placeholder='example@hotmail.com' id="usernameLogin" name="usernameLogin"></input><br/>
                    <label for="password">password</label>                    
                    <input type="password" placeholder="least 8 word" id="passwordLogin" name="passwordLogin"></input><br/>
                    <input type="button" value="Login" onClick={this.onSubmit.bind(this)}></input>
                </form>
            </div>
        )
    }
}
